/* eslint-disable no-restricted-globals */

// This service worker can be customized!
// See https://developers.google.com/web/tools/workbox/modules
// for the list of available Workbox modules, or add any other
// code you'd like.
// You can also remove this file if you'd prefer not to use a
// service worker, and the Workbox build step will be skipped.

import { clientsClaim } from 'workbox-core';
import { ExpirationPlugin } from 'workbox-expiration';
import { precacheAndRoute, createHandlerBoundToURL } from 'workbox-precaching';
import { registerRoute } from 'workbox-routing';
import { StaleWhileRevalidate } from 'workbox-strategies';

import { initializeApp } from "firebase/app";
import { getMessaging } from "firebase/messaging/sw";

clientsClaim();

// Precache all of the assets generated by your build process.
// Their URLs are injected into the manifest variable below.
// This variable must be present somewhere in your service worker file,
// even if you decide not to use precaching. See https://cra.link/PWA
precacheAndRoute(self.__WB_MANIFEST);

// Set up App Shell-style routing, so that all navigation requests
// are fulfilled with your index.html shell. Learn more at
// https://developers.google.com/web/fundamentals/architecture/app-shell
const fileExtensionRegexp = new RegExp('/[^/?]+\\.[^/]+$');
registerRoute(
  // Return false to exempt requests from being fulfilled by index.html.
  ({ request, url }) => {
    // If this isn't a navigation, skip.
    if (request.mode !== 'navigate') {
      return false;
    } // If this is a URL that starts with /_, skip.

    if (url.pathname.startsWith('/_')) {
      return false;
    } // If this looks like a URL for a resource, because it contains // a file extension, skip.

    if (url.pathname.match(fileExtensionRegexp)) {
      return false;
    } // Return true to signal that we want to use the handler.

    return true;
  },
  createHandlerBoundToURL(process.env.PUBLIC_URL + '/index.html')
);

// An example runtime caching route for requests that aren't handled by the
// precache, in this case same-origin .png requests like those from in public/
registerRoute(
  // Add in any other file extensions or routing criteria as needed.
  ({ url }) => url.origin === self.location.origin && url.pathname.endsWith('.png'), // Customize this strategy as needed, e.g., by changing to CacheFirst.
  new StaleWhileRevalidate({
    cacheName: 'images',
    plugins: [
      // Ensure that once this runtime cache reaches a maximum size the
      // least-recently used images are removed.
      new ExpirationPlugin({ maxEntries: 50 }),
    ],
  })
);

// This allows the web app to trigger skipWaiting via
// registration.waiting.postMessage({type: 'SKIP_WAITING'})
self.addEventListener('message', (event) => {
  if (event.data && event.data.type === 'SKIP_WAITING') {
    self.skipWaiting();
  }
});

// Any other custom service worker logic can go here.

const firebaseApp = initializeApp({
  aapiKey: "AIzaSyA7OGuIcqhZ4MpqboI7a3yM9ViTbh7efak",
  authDomain: "doorlockapp-6b97f.firebaseapp.com",
  databaseURL: "https://doorlockapp-6b97f-default-rtdb.asia-southeast1.firebasedatabase.app",
  projectId: "doorlockapp-6b97f",
  storageBucket: "doorlockapp-6b97f.appspot.com",
  messagingSenderId: "378668336240",
  appId: "1:378668336240:web:3ab8ca16dcb7da5e1ba0b5",
  measurementId: "G-VFHCDJFYHS"
});

const messaging = getMessaging(firebaseApp);

self.addEventListener('push', (event) => {
  console.log('Push notification received:', event);
  const data = event.data.json();

  // Customize the behavior of your PWA when a notification is received
  self.registration.showNotification(data.title, {
    body: data.message,
    icon: data.icon,
    data: { url: data.url }
  });
});

self.addEventListener('notificationclick', (event) => {
  console.log('Notification clicked:', event);
  const url = event.notification.data.url;

  event.notification.close();
});

messaging.onBackgroundMessage((payload) => {
  console.log('Foreground FCM message received:', payload);

  // Customize the behavior of your PWA when a foreground FCM message is received
  self.registration.showNotification(payload.notification.title, {
    body: payload.notification.body,
    icon: payload.notification.icon,
    data: { url: payload.notification.click_action }
  });
});

self.addEventListener('activate', (event) => {
  console.log('Service worker activated:', event);

  // Request permission from the user to receive notifications
  messaging.requestPermission().then(() => {
    console.log('Notification permission granted.');
    // Subscribe the user to FCM messaging
    messaging.getToken().then((token) => {
      console.log('FCM token:', token);
      // Send the token to your server to associate with the user
    });
  }).catch((err) => {
    console.log('Unable to get permission to notify.', err);
  });
});
